import React, { useState, useEffect } from 'react';
import { Users, Trash2, Search, UserCheck, UserX } from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

function UserList() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users');
      setUsers(response.data.data);
    } catch (error) {
      toast.error('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (irisId, name) => {
    try {
      await axios.delete(`/api/users/${irisId}`);
      toast.success(`Deleted ${name}`);
      setUsers(users.filter(u => u.iris_id !== irisId));
      setDeleteConfirm(null);
    } catch (error) {
      toast.error('Failed to delete user');
    }
  };

  const filteredUsers = users.filter(user =>
    user.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.iris_id?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.department?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="animate-fadeIn">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-white mb-2">Enrolled Users</h1>
          <p className="text-white/70">Manage registered users in the system</p>
        </div>
        <div className="mt-4 md:mt-0">
          <div className="relative">
            <Search className="w-5 h-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search users..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 pr-4 py-2 rounded-lg border border-gray-200 focus:outline-none focus:border-indigo-500 w-64"
            />
          </div>
        </div>
      </div>

      {/* User Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div className="card p-4 flex items-center">
          <div className="p-3 bg-indigo-100 rounded-lg mr-3">
            <Users className="w-6 h-6 text-indigo-600" />
          </div>
          <div>
            <p className="text-2xl font-bold">{users.length}</p>
            <p className="text-gray-500 text-sm">Total Users</p>
          </div>
        </div>
        <div className="card p-4 flex items-center">
          <div className="p-3 bg-green-100 rounded-lg mr-3">
            <UserCheck className="w-6 h-6 text-green-600" />
          </div>
          <div>
            <p className="text-2xl font-bold">{users.filter(u => u.is_active).length}</p>
            <p className="text-gray-500 text-sm">Active</p>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="card overflow-hidden">
        <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4">
          <h2 className="text-xl font-bold text-white flex items-center">
            <Users className="w-6 h-6 mr-2" />
            All Enrolled Users
            <span className="ml-2 px-2 py-1 bg-white/20 rounded text-sm">{filteredUsers.length}</span>
          </h2>
        </div>

        <div className="p-6">
          {loading ? (
            <div className="flex items-center justify-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-4 border-indigo-500 border-t-transparent"></div>
            </div>
          ) : filteredUsers.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Iris ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Department</th>
                    <th>Employee ID</th>
                    <th>Enrolled</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user, index) => (
                    <tr key={user.iris_id}>
                      <td className="text-gray-500">{index + 1}</td>
                      <td className="font-mono font-medium">{user.iris_id}</td>
                      <td className="font-medium">{user.name}</td>
                      <td className="text-gray-500">{user.email || '-'}</td>
                      <td>{user.department || '-'}</td>
                      <td>{user.employee_id || '-'}</td>
                      <td className="text-sm text-gray-500">
                        {user.enrolled_at ? new Date(user.enrolled_at).toLocaleDateString() : '-'}
                      </td>
                      <td>
                        <span className={`badge ${user.is_active ? 'badge-success' : 'badge-danger'}`}>
                          {user.is_active ? 'Active' : 'Inactive'}
                        </span>
                      </td>
                      <td>
                        {deleteConfirm === user.iris_id ? (
                          <div className="flex items-center space-x-2">
                            <button
                              onClick={() => handleDelete(user.iris_id, user.name)}
                              className="px-3 py-1 bg-red-500 text-white rounded text-sm hover:bg-red-600"
                            >
                              Confirm
                            </button>
                            <button
                              onClick={() => setDeleteConfirm(null)}
                              className="px-3 py-1 bg-gray-300 text-gray-700 rounded text-sm hover:bg-gray-400"
                            >
                              Cancel
                            </button>
                          </div>
                        ) : (
                          <button
                            onClick={() => setDeleteConfirm(user.iris_id)}
                            className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                            title="Delete user"
                          >
                            <Trash2 className="w-5 h-5" />
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-12 text-gray-500">
              <UserX className="w-16 h-16 mx-auto mb-4 opacity-50" />
              <p className="text-lg">
                {searchTerm ? 'No users match your search' : 'No users enrolled yet'}
              </p>
              <p className="text-sm">
                {searchTerm ? 'Try a different search term' : 'Enroll users to see them here'}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default UserList;
