import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Eye, Home, Camera, FileText, UserPlus, Users, Menu, X } from 'lucide-react';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { path: '/', icon: Home, label: 'Dashboard' },
    { path: '/mark-attendance', icon: Camera, label: 'Mark Attendance' },
    { path: '/reports', icon: FileText, label: 'Reports' },
    { path: '/enroll', icon: UserPlus, label: 'Enroll' },
    { path: '/users', icon: Users, label: 'Users' },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="navbar px-4 py-3">
      <div className="container mx-auto flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <Eye className="w-8 h-8 text-indigo-600" />
          <span className="text-xl font-bold text-indigo-600">Iris Attendance</span>
        </Link>

        {/* Desktop Navigation */}
        <div className="hidden md:flex items-center space-x-1">
          {navItems.map(({ path, icon: Icon, label }) => (
            <Link
              key={path}
              to={path}
              className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
                isActive(path)
                  ? 'bg-indigo-100 text-indigo-700 font-semibold'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <Icon className="w-5 h-5" />
              <span>{label}</span>
            </Link>
          ))}
        </div>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden p-2 rounded-lg hover:bg-gray-100"
          onClick={() => setIsOpen(!isOpen)}
        >
          {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
        </button>
      </div>

      {/* Mobile Navigation */}
      {isOpen && (
        <div className="md:hidden mt-2 pb-4 animate-slideIn">
          {navItems.map(({ path, icon: Icon, label }) => (
            <Link
              key={path}
              to={path}
              onClick={() => setIsOpen(false)}
              className={`flex items-center space-x-2 px-4 py-3 rounded-lg mx-2 mb-1 transition-all ${
                isActive(path)
                  ? 'bg-indigo-100 text-indigo-700 font-semibold'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <Icon className="w-5 h-5" />
              <span>{label}</span>
            </Link>
          ))}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
