import React, { useState, useEffect } from 'react';
import { Calendar, Download, Users, Clock, CheckCircle, AlertTriangle } from 'lucide-react';
import axios from 'axios';

function AttendanceReport() {
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [records, setRecords] = useState([]);
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecords();
  }, [date]);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`/api/attendance/records?date=${date}`);
      setRecords(response.data.data.records);
      setSummary(response.data.data.summary);
    } catch (error) {
      console.error('Error fetching records:', error);
    } finally {
      setLoading(false);
    }
  };

  const exportToCSV = () => {
    if (records.length === 0) return;
    
    const headers = Object.keys(records[0]);
    const csvContent = [
      headers.join(','),
      ...records.map(record => headers.map(h => record[h]).join(','))
    ].join('\n');
    
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `attendance_${date}.csv`;
    a.click();
  };

  const stats = summary ? [
    { label: 'Total Present', value: summary.total, icon: Users, color: 'text-indigo-600', bg: 'bg-indigo-100' },
    { label: 'On Time', value: summary.on_time, icon: CheckCircle, color: 'text-green-600', bg: 'bg-green-100' },
    { label: 'Late', value: summary.late, icon: AlertTriangle, color: 'text-amber-600', bg: 'bg-amber-100' },
    { label: 'Total Entries', value: summary.total_entries, icon: Clock, color: 'text-purple-600', bg: 'bg-purple-100' },
  ] : [];

  return (
    <div className="animate-fadeIn">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-white mb-2">Attendance Report</h1>
          <p className="text-white/70">View and export attendance records</p>
        </div>
        <div className="flex items-center space-x-4 mt-4 md:mt-0">
          <div className="flex items-center bg-white rounded-lg px-4 py-2 shadow">
            <Calendar className="w-5 h-5 text-gray-400 mr-2" />
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="border-none focus:outline-none"
            />
          </div>
          <button
            onClick={exportToCSV}
            disabled={records.length === 0}
            className="btn-primary flex items-center space-x-2"
          >
            <Download className="w-5 h-5" />
            <span>Export CSV</span>
          </button>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        {stats.map((stat, index) => (
          <div key={index} className="card p-4">
            <div className="flex items-center">
              <div className={`p-3 rounded-lg ${stat.bg} mr-4`}>
                <stat.icon className={`w-6 h-6 ${stat.color}`} />
              </div>
              <div>
                <p className="text-2xl font-bold">{stat.value}</p>
                <p className="text-gray-500 text-sm">{stat.label}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Records Table */}
      <div className="card overflow-hidden">
        <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4">
          <h2 className="text-xl font-bold text-white">
            Attendance Records - {new Date(date).toLocaleDateString('en-US', { 
              weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' 
            })}
          </h2>
        </div>

        <div className="p-6">
          {loading ? (
            <div className="flex items-center justify-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-4 border-indigo-500 border-t-transparent"></div>
            </div>
          ) : records.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Time</th>
                    <th>Name</th>
                    <th>Iris ID</th>
                    <th>Department</th>
                    <th>Type</th>
                    <th>Status</th>
                    <th>Confidence</th>
                  </tr>
                </thead>
                <tbody>
                  {records.map((record, index) => (
                    <tr key={index}>
                      <td className="text-gray-500">{index + 1}</td>
                      <td className="font-mono text-sm">
                        {new Date(record.timestamp).toLocaleTimeString()}
                      </td>
                      <td className="font-medium">{record.name}</td>
                      <td className="text-gray-500">{record.iris_id}</td>
                      <td>{record.department || '-'}</td>
                      <td>
                        <span className={`badge ${
                          record.entry_type === 'CHECK_IN' ? 'badge-success' : 'badge-info'
                        }`}>
                          {record.entry_type}
                        </span>
                      </td>
                      <td>
                        <span className={`badge ${
                          record.status === 'ON_TIME' ? 'badge-success' : 
                          record.status === 'LATE' ? 'badge-warning' : 'badge-info'
                        }`}>
                          {record.status}
                        </span>
                      </td>
                      <td>
                        <div className="flex items-center">
                          <div className="w-16 h-2 bg-gray-200 rounded-full mr-2">
                            <div 
                              className="h-2 bg-green-500 rounded-full" 
                              style={{ width: `${parseFloat(record.confidence) * 100}%` }}
                            ></div>
                          </div>
                          <span className="text-sm">
                            {(parseFloat(record.confidence) * 100).toFixed(1)}%
                          </span>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-12 text-gray-500">
              <Calendar className="w-16 h-16 mx-auto mb-4 opacity-50" />
              <p className="text-lg">No records for this date</p>
              <p className="text-sm">Select a different date or mark some attendance</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AttendanceReport;
