import React, { useState, useEffect } from 'react';
import { Users, Clock, CheckCircle, AlertTriangle, TrendingUp } from 'lucide-react';
import axios from 'axios';

function Dashboard() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [recentRecords, setRecentRecords] = useState([]);

  useEffect(() => {
    fetchDashboardData();
    fetchRecentRecords();
    
    // Refresh every 30 seconds
    const interval = setInterval(() => {
      fetchDashboardData();
      fetchRecentRecords();
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await axios.get('/api/dashboard');
      setData(response.data.data);
    } catch (error) {
      console.error('Error fetching dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentRecords = async () => {
    try {
      const response = await axios.get('/api/attendance/records');
      setRecentRecords(response.data.data.records.slice(-10).reverse());
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-4 border-indigo-500 border-t-transparent"></div>
      </div>
    );
  }

  const stats = [
    { 
      label: 'Total Present', 
      value: data?.summary?.total || 0, 
      icon: Users, 
      color: 'text-indigo-600',
      bgColor: 'bg-indigo-100'
    },
    { 
      label: 'On Time', 
      value: data?.summary?.on_time || 0, 
      icon: CheckCircle, 
      color: 'text-green-600',
      bgColor: 'bg-green-100'
    },
    { 
      label: 'Late', 
      value: data?.summary?.late || 0, 
      icon: AlertTriangle, 
      color: 'text-amber-600',
      bgColor: 'bg-amber-100'
    },
    { 
      label: 'Enrolled Users', 
      value: data?.enrolled_count || 0, 
      icon: TrendingUp, 
      color: 'text-purple-600',
      bgColor: 'bg-purple-100'
    },
  ];

  return (
    <div className="animate-fadeIn">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Dashboard</h1>
        <p className="text-white/70">
          {new Date().toLocaleDateString('en-US', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          })}
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {stats.map((stat, index) => (
          <div key={index} className="card p-6 animate-fadeIn" style={{ animationDelay: `${index * 0.1}s` }}>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500 text-sm font-medium">{stat.label}</p>
                <p className={`text-4xl font-bold mt-2 ${stat.color}`}>{stat.value}</p>
              </div>
              <div className={`p-4 rounded-full ${stat.bgColor}`}>
                <stat.icon className={`w-8 h-8 ${stat.color}`} />
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Recent Attendance */}
      <div className="card overflow-hidden">
        <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4">
          <h2 className="text-xl font-bold text-white flex items-center">
            <Clock className="w-6 h-6 mr-2" />
            Recent Attendance
          </h2>
        </div>
        <div className="p-6">
          {recentRecords.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>Time</th>
                    <th>Name</th>
                    <th>ID</th>
                    <th>Type</th>
                    <th>Status</th>
                    <th>Confidence</th>
                  </tr>
                </thead>
                <tbody>
                  {recentRecords.map((record, index) => (
                    <tr key={index} className="animate-slideIn" style={{ animationDelay: `${index * 0.05}s` }}>
                      <td className="font-mono text-sm">
                        {new Date(record.timestamp).toLocaleTimeString()}
                      </td>
                      <td className="font-medium">{record.name}</td>
                      <td className="text-gray-500">{record.iris_id}</td>
                      <td>
                        <span className={`badge ${record.entry_type === 'CHECK_IN' ? 'badge-success' : 'badge-info'}`}>
                          {record.entry_type}
                        </span>
                      </td>
                      <td>
                        <span className={`badge ${
                          record.status === 'ON_TIME' ? 'badge-success' : 
                          record.status === 'LATE' ? 'badge-warning' : 'badge-info'
                        }`}>
                          {record.status}
                        </span>
                      </td>
                      <td>
                        <div className="flex items-center">
                          <div className="w-20 h-2 bg-gray-200 rounded-full mr-2">
                            <div 
                              className="h-2 bg-green-500 rounded-full" 
                              style={{ width: `${parseFloat(record.confidence) * 100}%` }}
                            ></div>
                          </div>
                          <span className="text-sm text-gray-600">
                            {(parseFloat(record.confidence) * 100).toFixed(1)}%
                          </span>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-12 text-gray-500">
              <Clock className="w-16 h-16 mx-auto mb-4 opacity-50" />
              <p className="text-lg">No attendance records today</p>
              <p className="text-sm">Records will appear here as people mark attendance</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
