import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { UserPlus, Upload, Eye, CheckCircle, AlertCircle } from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

function EnrollUser() {
  const [formData, setFormData] = useState({
    iris_id: '',
    name: '',
    email: '',
    department: '',
    employee_id: '',
    phone: ''
  });
  const [preview, setPreview] = useState(null);
  const [detectedId, setDetectedId] = useState(null);
  const [detecting, setDetecting] = useState(false);
  const [enrolling, setEnrolling] = useState(false);

  const onDrop = useCallback(async (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      setPreview(URL.createObjectURL(file));
      setDetecting(true);

      const formDataUpload = new FormData();
      formDataUpload.append('image', file);

      try {
        const response = await axios.post('/api/iris/detect', formDataUpload, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        const data = response.data.data;
        setDetectedId(data.iris_id);
        setFormData(prev => ({ ...prev, iris_id: data.iris_id }));
        toast.success(`Detected Iris ID: ${data.iris_id} (${(data.confidence * 100).toFixed(1)}%)`);
      } catch (error) {
        toast.error('Could not detect iris ID from image');
      } finally {
        setDetecting(false);
      }
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: { 'image/*': ['.jpeg', '.jpg', '.png', '.bmp'] },
    maxFiles: 1
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.iris_id || !formData.name) {
      toast.error('Iris ID and Name are required');
      return;
    }

    setEnrolling(true);

    try {
      const response = await axios.post('/api/users/enroll', formData);
      toast.success(response.data.message);
      
      // Reset form
      setFormData({
        iris_id: '',
        name: '',
        email: '',
        department: '',
        employee_id: '',
        phone: ''
      });
      setPreview(null);
      setDetectedId(null);
    } catch (error) {
      toast.error(error.response?.data?.error || 'Enrollment failed');
    } finally {
      setEnrolling(false);
    }
  };

  return (
    <div className="animate-fadeIn">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Enroll User</h1>
        <p className="text-white/70">Register a new user in the attendance system</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Image Upload */}
        <div className="card overflow-hidden">
          <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4">
            <h2 className="text-xl font-bold text-white flex items-center">
              <Eye className="w-6 h-6 mr-2" />
              Step 1: Upload Iris Image
            </h2>
          </div>
          <div className="p-6">
            <div
              {...getRootProps()}
              className={`dropzone ${isDragActive ? 'active' : ''}`}
            >
              <input {...getInputProps()} />
              {preview ? (
                <img src={preview} alt="Preview" className="max-h-48 mx-auto rounded-lg" />
              ) : (
                <>
                  <Upload className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                  <p className="text-lg text-gray-600 mb-2">
                    {isDragActive ? 'Drop the image here' : 'Upload iris/eye image'}
                  </p>
                  <p className="text-sm text-gray-400">to auto-detect Iris ID</p>
                </>
              )}
            </div>

            {detecting && (
              <div className="flex items-center justify-center mt-4">
                <div className="animate-spin rounded-full h-6 w-6 border-2 border-indigo-500 border-t-transparent mr-2"></div>
                <span className="text-gray-600">Detecting iris ID...</span>
              </div>
            )}

            {detectedId && (
              <div className="mt-4 p-4 bg-green-50 rounded-lg border border-green-200">
                <div className="flex items-center text-green-700">
                  <CheckCircle className="w-5 h-5 mr-2" />
                  <span className="font-medium">Detected Iris ID: {detectedId}</span>
                </div>
              </div>
            )}

            <div className="mt-6 p-4 bg-amber-50 rounded-lg border border-amber-200">
              <div className="flex items-start">
                <AlertCircle className="w-5 h-5 text-amber-600 mr-2 mt-0.5" />
                <div className="text-sm text-amber-800">
                  <p className="font-medium mb-1">Tips for best results:</p>
                  <ul className="list-disc list-inside space-y-1">
                    <li>Take close-up photos of the eye</li>
                    <li>Ensure good lighting</li>
                    <li>Keep the eye fully open</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Enrollment Form */}
        <div className="card overflow-hidden">
          <div className="bg-gradient-to-r from-green-500 to-emerald-600 px-6 py-4">
            <h2 className="text-xl font-bold text-white flex items-center">
              <UserPlus className="w-6 h-6 mr-2" />
              Step 2: Enter Details
            </h2>
          </div>
          <div className="p-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Iris ID <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="text"
                    name="iris_id"
                    value={formData.iris_id}
                    onChange={handleChange}
                    className="input"
                    placeholder="Auto-detected or enter manually"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Full Name <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="input"
                    placeholder="John Doe"
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    className="input"
                    placeholder="john@example.com"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    className="input"
                    placeholder="+1234567890"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Department</label>
                  <select
                    name="department"
                    value={formData.department}
                    onChange={handleChange}
                    className="input"
                  >
                    <option value="">Select...</option>
                    <option value="Engineering">Engineering</option>
                    <option value="Sales">Sales</option>
                    <option value="Marketing">Marketing</option>
                    <option value="HR">Human Resources</option>
                    <option value="Finance">Finance</option>
                    <option value="IT">IT</option>
                    <option value="Operations">Operations</option>
                    <option value="Research">Research</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Employee ID</label>
                  <input
                    type="text"
                    name="employee_id"
                    value={formData.employee_id}
                    onChange={handleChange}
                    className="input"
                    placeholder="EMP001"
                  />
                </div>
              </div>

              <div className="pt-4">
                <button
                  type="submit"
                  disabled={enrolling}
                  className="btn-primary w-full flex items-center justify-center space-x-2"
                >
                  {enrolling ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent"></div>
                      <span>Enrolling...</span>
                    </>
                  ) : (
                    <>
                      <UserPlus className="w-5 h-5" />
                      <span>Enroll User</span>
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EnrollUser;
